import React, { useState } from 'react';
import UserList from './components/UserList';
import UserForm from './components/UserForm';
import './app'; // Asegúrate de tener un archivo App.css para definir estilos adicionales si es necesario

const App = () => {
    const [userToEdit, setUserToEdit] = useState(null);
    const [refresh, setRefresh] = useState(false);

    const selectUser = (user) => {
        setUserToEdit(user);
    };

    return (
        <div className="app-container"> {/* Contenedor principal */}
            <div className="app-content"> {/* Contenido de la aplicación */}
                <h1>My Hexagonal CRUD App</h1>
                <div className="user-management"> {/* Contenedor para la gestión de usuarios */}
                    <UserForm userToEdit={userToEdit} refresh={refresh} setRefresh={setRefresh} />
                    <UserList selectUser={selectUser} refresh={refresh} />
                </div>
            </div>
        </div>
    );
};

export default App;
